function RenderIcon(props: { iconId: number; className?: string }) {
  switch (props.iconId) {
    case 0:
      return <Icon1 className={props.className} />;
    case 1:
      return <Icon2 className={props.className} />;
    case 2:
      return <Icon3 className={props.className} />;
    default:
      return <Icon4 className={props.className} />;
  }
}

export default function SuccessPage() {
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    // Show message after delay
    setTimeout(() => setShowMessage(true), 500);
  }, []);

  return (
    <div
      className="relative min-h-screen w-full overflow-hidden bg-[#f4e6d6]"
      style={{
        fontFamily:
          'ui-sans-serif, system-ui, -apple-system, "Segoe UI", Roboto, Helvetica, Arial, sans-serif',
      }}
    >
      {/* Paper texture overlay */}
      <div className="paper-texture absolute inset-0 pointer-events-none" />


      {/* Main content */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen px-4 text-center">

        {/* Success message */}
        <div
          className={`transition-all duration-1000 ${
            showMessage ? "opacity-100 translate-y-0" : "opacity-0 translate-y-10"
          }`}
        >
          <h1
            className="text-[#b63338] text-4xl sm:text-5xl md:text-6xl lg:text-7xl mb-6 font-bold tracking-tight"
            style={{
              textShadow: "0px 4px 6px rgba(123, 30, 28, 0.25)",
            }}
          >
            Yay!
          </h1>

          <h2
            className="text-[#b63338] text-2xl sm:text-3xl md:text-4xl mb-6 font-semibold"
            style={{
              textShadow: "0px 4px 6px rgba(123, 30, 28, 0.25)",
            }}
          >
            I knew you&apos;d say yes!
          </h2>

          <p
            className="text-[#b63338] text-lg sm:text-xl max-w-2xl mx-auto mb-10 leading-relaxed"
            style={{
              textShadow: "0px 2px 4px rgba(123, 30, 28, 0.2)",
            }}
          >
            You&apos;ve made me the happiest person in the world!
            <br />
            Can&apos;t wait to spend Valentine&apos;s Day with you!
          </p>

          {/* Love note - using torn paper image */}
          <div className="relative mx-auto mb-8 w-[min(90vw,400px)]">
            <div
              className="relative"
              style={{
                transform: "rotate(-2deg)",
                aspectRatio: "352 / 359",
              }}
            >
              <Image
                src="/torn-paper.png"
                alt=""
                fill
                className="object-contain drop-shadow-xl"
                priority
              />
              <div className="absolute inset-0 flex items-center justify-center">
                {/* To: Mani */}
                <p
                  className="absolute text-white text-center italic text-xs sm:text-sm font-semibold"
                  style={{ transform: "translate(0px, -118px)" }}
                >
                  To: Mani
                </p>
                
                {/* Body text */}
                <p
                  className="absolute text-white text-center italic text-[10px] sm:text-xs leading-relaxed"
                  style={{ transform: "translate(4px, -49px)" }}
                >
                  You are the most amazing person I know.
                  <br />
                  Thank you for being my Valentine!
                </p>
                
                {/* Signature */}
                <p
                  className="absolute text-white text-center italic text-[10px] sm:text-xs leading-relaxed"
                  style={{ transform: "translate(14px, 64px)" }}
                >
                  With all my love,
                  <br />
                  Dhruv
                </p>
              </div>
            </div>
          </div>

          {/* Icon decoration */}
          <div className="flex justify-center gap-3 sm:gap-4">
            {[0, 1, 2, 3].map((iconId, i) => (
              <div
                key={i}
                className="animate-float"
                style={{ animationDelay: `${i * 0.25}s` }}
              >
                <RenderIcon iconId={iconId} className="h-10 w-10 sm:h-12 sm:w-12" />
              </div>
            ))}
          </div>
        </div>

        {/* Back button */}
        <Link
          href="/"
          className="mt-12 px-6 py-3 bg-[#b63338] text-white text-base sm:text-lg rounded-full shadow-lg hover:bg-[#9a2a2e] transition-all duration-300 hover:scale-105 font-semibold"
          style={{ textShadow: "0px 2px 4px rgba(0, 0, 0, 0.3)" }}
        >
          Back to Card
        </Link>
      </div>
    </div>
  );
}
